from qunet.qubit import Qubit
import networkx as nx
import matplotlib.pyplot as plt

R = 25
M = 5

class Core:
    qubits: list[Qubit] = []
    edges = []
    gates = []

    def plot(self):
        G = nx.Graph()
        for qubit in self.qubits:
            G.add_node(qubit.id)
        G.add_edges_from(Core.edges)
        nx.draw(G, with_labels=True)
        plt.show()



